private void PuntoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_PuntoActionPerformed
    String re=Result.getText();
    int tam=re.length();
    int band=0;
    for(int i=0;i<tam;i++){
        if(re.charAt(i)=='.'){
          band=1;  
        }
    }
    if(re.length()<25 && band==0){
        if(re.length()==0){
           String nre="0.";
            Result.setText(nre); 
        }else{
            String nre=re+".";
            Result.setText(nre);
            }
    }
}//GEN-LAST:event_PuntoActionPerformed

