private void IgualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_IgualActionPerformed
    String op=Result.getText();
    int tam=op.length();
    String num1="";
    String num2="";
    char operando='?';
    int bandop=0;
    try{
    for(int i=0;i<tam;i++){
        if(bandop==0){
            if(op.charAt(i)=='x' || op.charAt(i)=='-' || op.charAt(i)=='+' || op.charAt(i)=='/'){
                bandop=1;
                operando=op.charAt(i);
                }
            else{
                num1=num1+op.charAt(i);
                }
            }
        else{
            num2=num2+op.charAt(i);
            }
        }
    //System.out.println(num1);
    //System.out.println(num2);
    float n1=0,n2=0,res=0;
    n1=Float.parseFloat(num1);
    n2=Float.parseFloat(num2);
    String fin="";
    if(operando=='x'){
        res=n1*n2;
    }
    if(operando=='+'){
        res=n1+n2;
    }
    if(operando=='-'){
        res=n1-n2;
    }
    if(operando=='/'){
        res=n1/n2;
    }
    fin=String.valueOf(res);
    String text="";
    int max=fin.length()-1;
        for(int j=max;j>=0;j--){
            if(fin.charAt(j)=='0' || fin.charAt(j)=='.'){
                if(fin.charAt(j)=='.'){
                    fin=fin.substring(0, fin.length()-1);
                    break;
                }else{
                    fin=fin.substring(0, fin.length()-1);
                }
            }
            else{
                break;
                }
        }
    
    text=fin;
    if(fin.equals("Infinity")){
                text="Indefinido";
            }
   Result.setText(text);
    }
    catch(Exception e){
        System.out.println("Error");
    }
}//GEN-LAST:event_IgualActionPerformed

